import React from 'react'
import styled from 'styled-components'
import { createParameter } from 'typescript'
import './styles.css'

const TarefaList = styled.ul`
  padding: 0;
  width: 200px;
`

const Tarefa = styled.li`
  text-align: left;
  text-decoration: ${({ completa }) => (completa ? 'line-through' : 'none')};
`

const InputsContainer = styled.div`
  display: grid;
  grid-auto-flow: column;
  gap: 10px;
`

class App extends React.Component {
  state = {
    tarefas: [

      {
        id: Date.now(), // Explicação abaixo
        texto: 'Texto da tarefa',
        completa: false // Indica se a tarefa está completa (true ou false)
      },
      {
        id: Date.now(), // Explicação abaixo
        texto: 'Texto da segunda tarefa',
        completa: true // Indica se a tarefa está completa (true ou false)
      }

    ],
    inputValue: '',
    filtro: ''
  }

  componentDidUpdate() {

  };

  componentDidMount() {
    const tarefasSalvas = JSON.parse(localStorage.getItem("tarefas"))
    this.setState({ tarefas: tarefasSalvas });
  };

  onChangeInput = (event) => {
    this.setState({ inputValue: event.target.value });
  }

  criaTarefa = (event) => {

    const novaTarefa = [{ id: Date.now(), texto: 'teste', completa: false }]
    const copiaDoEstado = [...this.state.tarefas, novaTarefa]
    copiaDoEstado.push(novaTarefa)

    this.setState({ tarefas: copiaDoEstado });
  }

  selectTarefa = (id) => {

    const novaListaTarefas = this.state.tarefas.map((tarefa) => {
      if (tarefa.id === id){
        const newTask = {
          ...tarefa,
          completa: !tarefa.completa
        }
         return newTask
      } else {
        return tarefa
      }
    })
    this.setState({ tarefas: novaListaTarefas });
  }

  onChangeFilter = (event) => {

  }

  render() {
    const listaFiltrada = this.state.tarefas.filter(tarefa => {
      switch (this.state.filtro) {
        case 'pendentes':
          return !tarefa.completa
        case 'completas':
          return tarefa.completa
        default:
          return true
      }
    })

    return (
      <div className="App">
        <h1>Lista de tarefas</h1>
        <InputsContainer>
          <input value={this.state.inputValue} onChange={this.onChangeInput} />
          <button onClick={this.criaTarefa}>Adicionar</button>
        </InputsContainer>
        <br />

        <InputsContainer>
          <label>Filtro</label>
          <select value={this.state.filter} onChange={this.onChangeFilter}>
            <option value="">Nenhum</option>
            <option value="pendentes">Pendentes</option>
            <option value="completas">Completas</option>
          </select>
        </InputsContainer>
        <TarefaList>
          {listaFiltrada.map(tarefa => {
            return (
              <Tarefa
                completa={tarefa.completa}
                onClick={() => this.selectTarefa(tarefa.id)}
              >
                {tarefa.texto}
              </Tarefa>
            )
          })}
        </TarefaList>
      </div>
    )
  }
}

export default App
